def lps(s):
    n = len(s)
    table = [[0 for x in range(n)] for y in range(n)]
    for i in range(n):
        table[i][i] = 1
    maxLength = 1
    i = 0
    while i < n-1:
        if s[i] == s[i+1]:
            table[i][i+1] = 2
            maxLength = 2
        else:
            table[i][i+1] = 0
        i = i + 1
    k = 3
    while k <= n:
        for i in range(n-k+1):
            j = i + k - 1
            if table[i+1][j-1] == 1 or table[i][j] == k-2:
                table[i][j] = max(table[i][j-1], table[i+1][j])
            else:
                table[i][j] = table[i+1][j-1] + 2
            if table[i][j] > maxLength:
                maxLength = table[i][j]
        k = k + 1
    return maxLength

