def eggDrop(n, k):
    if k == 1 or k == 0:
        return k
    if n == 1:
        return k
    min_attempts = float('inf')
    for x in range(1, k + 1):
        attempts = max(eggDrop(n - 1, x - 1), eggDrop(n, k - x))
        if attempts < min_attempts:
            min_attempts = attempts
    return min_attempts + 1

n = 2
k = 10
print(eggDrop(n, k))
