def stone_game_winner(w):
    n = len(w)

    # Calculate the prefix sum of the stones
    psum = [0] * (n + 1)
    for i in range(n):
        psum[i + 1] = psum[i] + w[i]

    # Check if the current player can win
    for i in range(n - 1, 0, -1):
        if psum[i] - psum[0] < psum[n] - psum[i]:
            return "First"

    return "Second"
