def is_scramble(s1, s2):
    if sorted(s1)!= sorted(s2):
        return False
    if len(s1)!= len(s2):
        return False
    if len(s1) == 0:
        return True
    for i in range(len(s1)):
        if s1[i] == s2[0]:
            t = s2[1:]
            if t[0] == s1[i+1:]:
                return is_scramble(s1[:i]+s1[i+1:], t)
    return False
