def min_falling_path_sum(grid):
    m, n = len(grid), len(grid[0])
    dp = [[0] * n for _ in range(m)]
    dp[-1] = grid[-1][:]
    for i in range(m - 2, -1, -1):
        for j in range(n):
            dp[i][j] = grid[i][j] + min(dp[i + 1][j], dp[i + 1][j - 1] if j else float('inf'))
    return dp[0][0]
