import heapq
import os
from collections import defaultdict

class Node:
    def __init__(self, char, freq):
        self.char = char
        self.freq = freq
        self.left = None
        self.right = None

    def __lt__(self, other):
        return self.freq < other.freq

class HuffmanCoding:
    def __init__(self):
        self.codes = {}

    def get_codes(self, root, code):
        if root is None:
            return
        if type(root) == str:
            self.codes[root] = code
        else:
            self.get_codes(root.left, code + "0")
            self.get_codes(root.right, code + "1")

    def compress(self, text):
        freq = defaultdict(int)
        for char in text:
            freq[char] += 1
        pq = []
        for char, f in freq.items():
            node = Node(char, f)
            heapq.heappush(pq, node)
        while len(pq) > 1:
            lo = heapq.heappop(pq)
            hi = heapq.heappop(pq)
            node = Node(None, lo.freq + hi.freq)
            node.left = lo
            node.right = hi
            heapq.heappush(pq, node)
        root = None
        while pq:
            root = heapq.heappop(pq)
        self.get_codes(root, "")
        return "".join(self.codes[char] for char in text)

    def decompress(self, compressed):
        decompressed = ""
        code = ""
        for char in compressed:
            code += char
            for char, code_bits in self.codes.items():
                if code_bits == code:
                    decompressed += char
                    code = ""
                    break
        return decompressed
