def egyptian_fraction(n):
    def gcd(a, b):
        while b:
            a, b = b, a % b
        return a

    def egyptian_fraction_representation(a, b):
        if a == b:
            return []
        if a < b:
            return egyptian_fraction_representation(b, a)
        return [1, 1, *egyptian_fraction_representation(a - 1, b)]

    def find_representation(n):
        return egyptian_fraction_representation(n, 1)

    return find_representation(n)

