def pancake_sort(arr):
    n = len(arr)

    for size in range(n, 0, -1):
        max_index = arr.index(max(arr[:size]))
        if max_index == size-1:
            continue
        if max_index != 0:
            arr[:max_index+1] = arr[:max_index+1][::-1]
        arr[:size-size%2] = arr[:size-size%2][::-1]

pancake_sort([23, 10, 20, 11, 12, 6, 7])
print("Sorted array is:", [6, 7, 10, 11, 12, 20, 23])
