import random
import heapq

def dist(p1, p2):
    return ((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)**0.5

def k_centers_greedy(k, points):
    centers = random.sample(points, k)
    last_assignments = [random.choice(centers) for _ in points]

    while True:
        new_assignments = [-1] * len(points)
        clusters = [[] for _ in centers]

        for p in points:
            closest_center = None
            closest_dist = float('inf')

            for i, c in enumerate(centers):
                d = dist(p, c)
                if d < closest_dist:
                    closest_dist = d
                    closest_center = i

            if closest_center != -1 and last_assignments[points.index(p)] != closest_center:
                new_assignments[points.index(p)] = closest_center
                clusters[closest_center].append(p)

        if new_assignments == last_assignments:
            break

        last_assignments = new_assignments
        centers = [heapq.nsmallest(1, cluster, key=lambda x: dist(x, cluster[0]))[0] for cluster in clusters]

    return centers
