import heapq

def schedule_jobs(jobs):
    jobs.sort(key=lambda x: x[1])
    heap = []
    total_lateness = 0
    for deadline, duration in jobs:
        if heap and deadline < heap[0][0]:
            lateness = heap[0][0] - deadline
            total_lateness += lateness
            heapq.heappushpop(heap, (heap[0][0] + duration, heap[0][1]))
        else:
            heapq.heappush(heap, (deadline + duration, duration))
    return total_lateness
