class Solution:
    def mincostTickets(self, days: List[int], costs: List[int]) -> int:
        n = len(days)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0
        for i in range(1, n + 1):
            dp[i] = min(dp[i - 1] + costs[0], dp[max(0, bisect.bisect_right(days, days[i - 1] - 7)) ] + costs[1], dp[max(0, bisect.bisect_right(days, days[i - 1] - 30))] + costs[2])
        return dp[n]
