def minimum_points(grid):
    if not grid or not grid[0]:
        return 0

    dp = [[float('inf')] * len(grid[0]) for _ in range(len(grid))]
    dp[-1][-1] = grid[-1][-1]

    for i in range(len(grid) - 1, -1, -1):
        for j in range(len(grid[0]) - 1, -1, -1):
            if i == len(grid) - 1 and j == len(grid[0]) - 1:
                continue
            else:
                dp[i][j] = min(dp[i][j], dp[i + 1][j], dp[i][j + 1]) if i < len(grid) - 1 and j < len(grid[0]) - 1 else min(dp[i][j], dp[i + 1][j], dp[i][j + 1]) - grid[i][j]

    return dp[0][0] + grid[0][0] if dp[0][0] != float('inf') else -1
