def chocolate_distribution(chocolates, students):
    if len(chocolates) < students:
        return -1
    chocolates.sort()
    min_diff = float('inf')
    for i in range(len(chocolates)-students+1):
        diff = chocolates[i+students-1] - chocolates[i]
        if diff < min_diff:
            min_diff = diff
    return min_diff
