def minCost(n, m):
    horizontal_cuts = sorted(n)
    vertical_cuts = sorted(m)

    h_cuts = [horizontal_cuts[0]]
    for i in range(1, len(horizontal_cuts)):
        h_cuts.append(horizontal_cuts[i] - horizontal_cuts[i-1])

    v_cuts = [vertical_cuts[0]]
    for i in range(1, len(vertical_cuts)):
        v_cuts.append(vertical_cuts[i] - vertical_cuts[i-1])

    return min(h_cuts) * min(v_cuts)
