import time
import threading

class TaskScheduler:
    def __init__(self):
        self.tasks = []
        self.cooldown = 0

    def add_task(self, task):
        self.tasks.append(task)

    def start(self):
        while True:
            for task in self.tasks:
                time.sleep(self.cooldown)
                task()
                self.cooldown = 0
            self.cooldown = 5  # Set the cooldown timer

    def run(self):
        threading.Thread(target=self.start).start()

class Task:
    def __init__(self, func, cooldown):
        self.func = func
        self.cooldown = cooldown

    def __call__(self):
        self.func()
        self.cooldown -= 1

def job1():
    print("Job 1")

def job2():
    print("Job 2")

scheduler = TaskScheduler()
scheduler.add_task(Task(job1, 5))
scheduler.add_task(Task(job2, 10))
scheduler.run()
