import numpy as np

def min_max_heights(arr, k):
    arr.sort()
    n = len(arr)
    diff = arr[n-1] - arr[0]
    small = arr[0] + k
    big = arr[n-1] - k
    if small > big:
        small, big = big, small
    for i in range(1, n-1):
        sub = arr[i] - k
        add = arr[i] + k
        if sub >= small or add <= big:
            continue
        if big - sub <= add - small:
            small = sub
        else:
            big = add
    return min(diff, big - small)
