def solve_sudoku(board):
    def find_empty(bo):
        for i in range(9):
            for j in range(9):
                if bo[i][j] == 0:
                    return i, j
        return None, None

    def is_valid(bo, num, pos):
        # Check row
        for i in range(9):
            if bo[pos[0]][i] == num and pos[1] != i:
                return False

        # Check column
        for i in range(9):
            if bo[i][pos[1]] == num and pos[0] != i:
                return False

        # Check box
        box_x = pos[1] // 3
        box_y = pos[0] // 3

        for i in range(box_y*3, box_y*3 + 3):
            for j in range(box_x * 3, box_x*3 + 3):
                if bo[i][j] == num and (i,j) != pos:
                    return False

        return True

    def solve(bo):
        find = find_empty(bo)
        if find[0] is None:
            return True

        row, col = find

        for i in range(1,10):
            if is_valid(bo, i, (row, col)):
                bo[row][col] = i

                if solve(bo):
                    return True

                bo[row][col] = 0

        return False

    solve(board)
    return board
