def word_search(board, word):
    for i in range(len(board)):
        for j in range(len(board[i])):
            if dfs(board, i, j, word, 0):
                return True
    return False

def dfs(board, i, j, word, k):
    if k == len(word):
        return True
    if i<0 or i>=len(board) or j<0 or j>=len(board[i]) or word[k]!=board[i][j]:
        return False
    temp = board[i][j]
    board[i][j] = "#"
    res = dfs(board, i+1, j, word, k+1) or dfs(board, i-1, j, word, k+1) or dfs(board, i, j+1, word, k+1) or dfs(board, i, j-1, word, k+1)
    board[i][j] = temp
    return res
