def combinationSum(candidates, target):
    res = []
    def dfs(remain, comb, start):
        if remain == 0:
            res.append(list(comb))
            return
        for i in range(start, len(candidates)):
            if candidates[i] > remain:
                break
            comb.append(candidates[i])
            dfs(remain - candidates[i], comb, i)
            comb.pop()
    dfs(target, [], 0)
    return res
