def odd_even_sort(arr):
    n = len(arr)
    flag = True
    while flag:
        flag = False
        for i in range(0, n-1, 2):
            if arr[i] > arr[i+1]:
                arr[i], arr[i+1] = arr[i+1], arr[i]
                flag = True
        for i in range(1, n-1, 2):
            if arr[i] > arr[i+1]:
                arr[i], arr[i+1] = arr[i+1], arr[i]
                flag = True

arr = [5, 3, 8, 4, 6]
odd_even_sort(arr)
