def is_valid_word(word, letters):
    for char in word:
        if char not in letters:
            return False
    return True

def solve_puzzle(equation, letters):
    for a in range(10):
        for b in range(10):
            for c in range(10):
                if is_valid_word(str(a), letters) and is_valid_word(str(b), letters) and is_valid_word(str(c), letters):
                    if str(a)+str(b)+str(c) == equation:
                        return str(a) + ' + ' + str(b) + ' = ' + str(c)

equation = 'abc + abd = abc'
letters = 'abcdef'
print(solve_puzzle(equation, letters))
