import math

def partition_into_k_subsets(arr, k):
    total_sum = sum(arr)
    if total_sum % k != 0:
        return []
    target_sum = total_sum // k
    n = len(arr)
    used = [False] * n
    result = [[] for _ in range(k)]
    return backtrack(arr, 0, target_sum, result, used)

def backtrack(arr, start, target_sum, result, used):
    if all(used):
        for subset in result:
            if sum(subset) != target_sum:
                return []
        return result
    for i in range(start, len(arr)):
        if not used[i] and sum(result[-1]) + arr[i] <= target_sum:
            result[-1].append(arr[i])
            used[i] = True
            if len(result[-1]) == target_sum:
                result.append([])
            if backtrack(arr, i + 1, target_sum, result, used):
                return result
            used[i] = False
            if len(result[-1]) == target_sum:
                result.pop()
            result[-1].remove(arr[i])
    return []
