def is_palindrome(s):
    return s == s[::-1]

def palindrome_partition(s):
    def backtrack(start, path):
        if start == len(s):
            res.append(path)
            return
        for i in range(start, len(s)):
            if is_palindrome(s[start:i+1]):
                backtrack(i+1, path + [s[start:i+1]])

    res = []
    backtrack(0, [])
    return res

