def is_additive_number(s):
    def is_valid(num1, num2, start):
        if start == len(s):
            return True
        num3 = num1 + num2
        if s.startswith(str(num3), start):
            return is_valid(num2, num3, start+len(str(num3)))
        return False

    for i in range(1, len(s)//2 + 1):
        for j in range(1, len(s)//2 + 1):
            if s[0] == '0' and i > 1: 
                continue
            if s[i] == '0' and j > 1: 
                continue
            num1 = int(s[:i])
            num2 = int(s[i:i+j])
            if is_valid(num1, num2, i+j):
                return True
    return False

