def beautiful_arrangement_count(n):
    mod = 10 ** 9 + 7
    def can place(x, used):
        return all(x % (i + 1) != 0 for i in used) and all((x - 1) % (i + 1) != 0 for i in used)

    @cache
    def dp(pos, used):
        if pos == n:
            return 1
        ans = 0
        for i in range(n):
            if not (i in used or not can_place(i, used)):
                used.add(i)
                ans = (ans + dp(pos + 1, used)) % mod
                used.remove(i)
        return ans

    return dp(0, set())
