def partition(arr):
    n = len(arr)
    target_sum = sum(arr) // 2
    dp = [[False] * (target_sum + 1) for _ in range(n + 1)]
    dp[0][0] = True
    for i in range(1, n + 1):
        for j in range(target_sum + 1):
            if j >= arr[i - 1]:
                dp[i][j] = dp[i - 1][j] or dp[i - 1][j - arr[i - 1]]
            else:
                dp[i][j] = dp[i - 1][j]
    for j in range(target_sum, -1, -1):
        if dp[n][j]:
            return j
    return None
