def remove(string):
    while "()" in string:
        string = string.replace("()", "")
    return string

def isvalid(string):
    count = 0
    for char in string:
        if char == "(":
            count += 1
        elif char == ")":
            count -= 1
            if count < 0:
                return False
    return count == 0

def removeInvalid(string):
    for i in range(len(string)):
        for j in range(i, len(string)):
            if isvalid(string[0:i] + string[j+1:]):
                return removeInvalid(string[0:i] + string[j+1:])
    return remove(string)

