def optimal_balancing(transactions, budget):
    n = len(transactions)
    dp = [[0] * (budget + 1) for _ in range(n + 1)]
    for i in range(1, n + 1):
        for j in range(1, budget + 1):
            if transactions[i - 1] <= j:
                dp[i][j] = max(dp[i - 1][j], dp[i - 1][j - transactions[i - 1]] + transactions[i - 1])
            else:
                dp[i][j] = dp[i - 1][j]
    return dp[n][budget]
