def matchsticks_to_square(matchsticks):
    total_matchsticks = sum(matchsticks)
    if total_matchsticks % 4 != 0:
        return -1
    side_length = total_matchsticks // 4
    matchsticks.sort(reverse=True)
    used = [False] * len(matchsticks)
    def backtrack(index, count, sides):
        if count == 4 and all(side == side_length for side in sides):
            return True
        if count == 4:
            return False
        for i in range(index, len(matchsticks)):
            if not used[i] and sides[count] + matchsticks[i] <= side_length:
                used[i] = True
                if backtrack(i + 1, count + (sides[count] + matchsticks[i] == side_length), sides[:count] + [sides[count] + matchsticks[i]]):
                    return True
                used[i] = False
        return False
    return 0 if backtrack(0, 0, [0, 0, 0, 0]) else -1
