class Solution:
    def maxGold(self, grid):
        m, n = len(grid), len(grid[0])
        dp = [[0] * n for _ in range(m)]
        for i in range(m):
            for j in range(n):
                if grid[i][j] > 0:
                    if i == 0:
                        dp[i][j] = grid[i][j]
                    else:
                        dp[i][j] = grid[i][j] + max(dp[i-1][j-1] if j > 0 else 0, dp[i-1][j], dp[i-1][j+1] if j < n-1 else 0)
        return max(dp[-1])
