def find_median(arr):
    arr.sort()
    n = len(arr)
    return arr[n//2]

def median_of_medians(arr, k):
    if len(arr) <= 5:
        return find_median(arr)

    groups = [arr[i:i+5] for i in range(0, len(arr), 5)]
    medians = [find_median(group) for group in groups]

    pivot = median_of_medians(medians, len(medians)//2)

    lower = [x for x in arr if x < pivot]
    upper = [x for x in arr if x > pivot]
    equal = [x for x in arr if x == pivot]

    if k < len(lower):
        return median_of_medians(lower, k)
    elif k < len(lower) + len(equal):
        return pivot
    else:
        return median_of_medians(upper, k - len(lower) - len(equal))
