def kth_smallest(a, b, k):
    return sorted(a + b)[k - 1]

def find_kth_smallest(sorted_list1, sorted_list2, k):
    return sorted(sorted_list1 + sorted_list2)[k - 1]

def find_kth_smallest_iterative(sorted_list1, sorted_list2, k):
    merged = []
    i = j = 0
    while i < len(sorted_list1) and j < len(sorted_list2):
        if sorted_list1[i] < sorted_list2[j]:
            merged.append(sorted_list1[i])
            i += 1
        else:
            merged.append(sorted_list2[j])
            j += 1
    merged += sorted_list1[i:]
    merged += sorted_list2[j:]
    return merged[k - 1]

