def generate_subsets(nums):
    n = len(nums)
    subsets = []
    for i in range(2**n):
        subset = []
        for j in range(n):
            if (i >> j) & 1:
                subset.append(nums[j])
        subsets.append(subset)
    return subsets

# Example
nums = [1, 2, 3]
print(generate_subsets(nums))
