def is_perfect_number(n):
    if n <= 0:
        return False
    for i in range(1, n):
        if n % i == 0:
            sum = 0
            for j in range(1, i):
                if i % j == 0:
                    sum += j
            for k in range(i+1, n):
                if n % k == 0:
                    sum += k
            if sum == n:
                return True
    return False

print(is_perfect_number(6))  # True
print(is_perfect_number(28))  # True
print(is_perfect_number(12))  # False
