def introsort(arr, depth_limit=2*math.log(len(arr), 2)):
    def quicksort(arr, low, high, depth_limit):
        if high - low <= depth_limit:
            heapsort(arr, low, high)
            return
        pivot_index = (low + high) // 2
        arr[pivot_index], arr[high] = arr[high], arr[pivot_index]
        pivot_index = partition(arr, low, high)
        quicksort(arr, low, pivot_index-1, depth_limit)
        quicksort(arr, pivot_index+1, high, depth_limit)

    def partition(arr, low, high):
        pivot = arr[high]
        i = low - 1
        for j in range(low, high):
            if arr[j] < pivot:
                i += 1
                arr[i], arr[j] = arr[j], arr[i]
        arr[i+1], arr[high] = arr[high], arr[i+1]
        return i+1

    def heapsort(arr, low, high):
        def sift_down(arr, start, end):
            root = start
            while True:
                child = 2 * root + 1
                if child > end:
                    break
                if child+1 <= end and arr[child] < arr[child+1]:
                    child += 1
                if arr[root] < arr[child]:
                    arr[root], arr[child] = arr[child], arr[root]
                    root = child
                else:
                    break

        for i in range((len(arr) - 2) // 2, -1, -1):
            sift_down(arr, i, len(arr) - 1)
        for i in range(len(arr) - 1, -1, -1):
            arr[0], arr[i] = arr[i], arr[0]
            sift_down(arr, 0, i-1)

    quicksort(arr, 0, len(arr) - 1, depth_limit)
