def patience_sort(deck):
    if len(deck) <= 1:
        return deck
    else:
        piles = [[deck[0]]]
        for card in deck[1:]:
            if card > piles[-1][-1]:
                piles[-1].append(card)
            else:
                for i, pile in enumerate(piles):
                    if card <= pile[-1]:
                        piles[i].append(card)
                        break
                else:
                    piles.append([card])
        return [card for pile in piles for card in pile]

print(patience_sort([5, 2, 9, 1, 7, 6, 3, 8, 4]))
