import random
import math

def baby_step_giant_step(a, b, p):
    m = int(math.sqrt(p - 1))
    baby_steps = [pow(a, j, p) for j in range(m + 1)]
    giant_steps = {}
    for i, bbs in enumerate(baby_steps):
        for j in range(m):
            if pow(bbs * pow(a, j, p), 1, p) in giant_steps:
                return i * m + giant_steps[pow(bbs * pow(a, j, p), 1, p)]
        giant_steps[bbs] = i

def discrete_log(a, b, p):
    return baby_step_giant_step(a, b, p)

# example usage
p = 257
a = 2
b = 16
print(discrete_log(a, b, p))
