def pollard_p1(n):
    if n < 2:
        return []
    factors = []
    for a in range(2, n):
        x = a
        y = a
        d = 1
        while d == 1:
            x = (x * x + 1) % n
            y = (y * y + 1) % n
            y = (y * y + 1) % n
            d = gcd(abs(x - y), n)
            if d == n:
                break
        if d != 1 and d != n:
            factors.append(d)
            n = n // d
    if n > 1:
        factors.append(n)
    return factors

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

n = int(input("Enter a number: "))
print(pollard_p1(n))
