import math

def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def quadratic_sieve(n):
    a = int(math.ceil(math.sqrt(n)))
    b = 0
    while True:
        x = a ** 2 - n
        if is_prime(x):
            return a, b
        b += 1
        a = int(math.ceil(math.sqrt(n + b ** 2)))

print(quadratic_sieve(123456789))
