class Node:
    def __init__(self, data):
        self.data = data
        self.next = None

def strandSort(head):
    if not head or not head.next:
        return head

    sorted_lists = []
    current = head
    while current:
        new_list = [current.data]
        next_node = current.next
        while next_node and next_node.data >= new_list[-1]:
            new_list.append(next_node.data)
            next_node = next_node.next
        current.next = next_node
        sorted_lists.append(Node(new_list[0]))
        for i in range(1, len(new_list)):
            sorted_lists[-1].next = Node(new_list[i])
            sorted_lists[-1] = sorted_lists[-1].next
        current = next_node

    merged_list = sorted_lists[0] if sorted_lists else None
    for i in range(1, len(sorted_lists)):
        temp = merged_list
        while temp.next:
            temp = temp.next
        temp.next = sorted_lists[i]

    return merged_list
