def ZAlgorithm(s):
    n = len(s)
    z = [0] * n
    z[0] = n
    left, right = 0, 0
    for i in range(1, n):
        if i <= right:
            z[i] = min(right - i + 1, z[i - left])
        while i + z[i] < n and s[i + z[i]] == s[i - z[i]]:
            z[i] += 1
        if z[i] > 0:
            left, right = i, i + z[i] - 1
    return z
