def longestPalindrome(s):
    n=len(s)
    dp=[0]*n
    center=0
    right=0
    for i in range(n):
        if i<=right:
            dp[i]=min(dp[2*center-i],right-i)
        while i-dp[i]>=0 and i+dp[i]<n and s[i-dp[i]]==s[i+dp[i]]:
            dp[i]+=1
        if i+dp[i]>right:
            center=i
            right=i+dp[i]
    return max(dp)
