def bead_sort(arr):
    if any(not isinstance(x, int) or x < 0 for x in arr):
        return "Listede negatif olmayan tamsayılar olmalıdır."

    if not arr:
        return []

    max_num = max(arr)
    beads = [[1 if j <= i else 0 for j in range(max_num)] for i in arr]

    for j in range(max_num):
        sum = 0
        for i in range(len(arr)):
            sum += beads[i][j]
            beads[i][j] = 0

        for i in range(len(arr) - sum, len(arr)):
            beads[i][j] = 1

    result = []
    for i in range(len(arr)):
        value = 0
        for j in range(max_num):
            if beads[i][j]:
                value += 1
        result.append(value)

    return result

# Test
arr = [5, 3, 1, 7, 4, 1, 1]
print(bead_sort(arr))
