class SuffixTree:
    def __init__(self, text):
        self.text = text + '$'
        self.tree = {}

    def build_tree(self):
        for i in range(len(self.text)):
            suffix = self.text[i:]
            current = self.tree
            for j in range(0, len(suffix), 1):
                node = current.setdefault(suffix[j], {})
                if j == len(suffix) - 1:
                    node['$'] = i
                current = node

    def print_tree(self):
        def print_node(node, level=0):
            if '$' in node:
                print('  ' * level + str(node['$']))
            for char, child in node.items():
                print_node(child, level + 1)
        print_node(self.tree)
