def min_window(s, t):
    need, missing = dict(), len(t)
    for c in t: need[c] = need.get(c, 0) + 1

    i, ans_start, ans_len = 0, 0, float('inf')
    for j, c in enumerate(s):
        if c in need:
            need[c] -= 1
            if need[c] >= 0:
                missing -= 1
        while missing == 0:
            if j - i + 1 < ans_len:
                ans_start, ans_len = i, j - i + 1
            if s[i] in need:
                need[s[i]] += 1
                if need[s[i]] > 0:
                    missing += 1
            i += 1
    return s[ans_start:ans_start + ans_len]
