def count_and_say(n):
    def count_and_say_sequence(sequence):
        result = []
        i = 0
        while i < len(sequence):
            count = 1
            while i + 1 < len(sequence) and sequence[i] == sequence[i + 1]:
                i += 1
                count += 1
            result.append(str(count) + sequence[i])
            i += 1
        return ''.join(result)

    sequence = '1'
    for _ in range(n):
        sequence = count_and_say_sequence(sequence)
    return sequence

