def smoothsort(arr):
    def adjust(arr, i, w):
        while i * 2 + 1 < len(arr):
            min_index = i * 2 + 1
            if i * 2 + 2 < len(arr) and arr[i * 2 + 2][1] < arr[min_index][1]:
                min_index = i * 2 + 2
            if arr[min_index][1] >= arr[i][1]:
                break
            arr[i], arr[min_index] = arr[min_index], arr[i]
            i = min_index

    n = len(arr)
    if n <= 1:
        return arr

    pq = [(arr[i], i) for i in range(n)]
    w = 2
    while 1 << (w - 1) < n:
        w += 1

    i = n // (2 ** (w - 1))
    while i < len(pq):
        adjust(pq, i, w)
        i += 1

    i = len(pq) - 1
    while i >= 0:
        arr[pq[0][1]], arr[i] = arr[i], arr[pq[0][1]]
        adjust(pq, 0, 1)
        i -= 1

