class Node:
    def __init__(self, data):
        self.data = data
        self.next = None

def delete(head, x):
    if head is None:
        return head

    if head.data == x:
        head = head.next
        return head

    current = head
    while current.next is not None:
        if current.next.data == x:
            current.next = current.next.next
            return head
        current = current.next

    return head
