class Node:
    def __init__(self, x):
        self.val = x
        self.next = None
        self.random = None

def copyRandomList(head):
    if not head:
        return None

    oldToNew = {}
    oldToNew[head] = Node(head.val)
    cur = head
    while cur:
        oldToNew[cur] = Node(cur.val)
        cur = cur.next

    cur = head
    while cur:
        if cur.next:
            oldToNew[cur].next = oldToNew[cur.next]
        if cur.random:
            oldToNew[cur].random = oldToNew[cur.random]
        cur = cur.next

    return oldToNew[head]
