class Node:
    def __init__(self, data=0, next=None):
        self.data = data
        self.next = next

def merge_sorted_lists(left, right):
    result = Node()
    last = result
    while left is not None and right is not None:
        if left.data < right.data:
            last.next = left
            left = left.next
        else:
            last.next = right
            right = right.next
        last = last.next
    last.next = left if left is not None else right
    return result.next

def merge_sort(head):
    if head is None or head.next is None:
        return head
    slow, fast = head, head.next
    while fast and fast.next:
        slow = slow.next
        fast = fast.next.next
    mid = slow.next
    slow.next = None
    return merge_sorted_lists(merge_sort(head), merge_sort(mid))
